SELECT TO_STRING(UTCNOW(), 'MM-d-y') as "Data do Processamento dos Dados", SUM(CAST(s."Vendas de Biodiesel" as FLOAT)) as "Total de Produtos Vendidos", AVG(CAST(s."Vendas de Biodiesel" as FLOAT)) as "Média de Produtos Vendidos por Venda", COUNT(*) as "Qtd. de Vendas", SUM(CASE WHEN s."Região Origem" = 'NORDESTE' AND s."Região Destino" = 'SUDESTE' THEN 1 ELSE 0 END) as "Qtd. de Vendas nordeste/sudeste" FROM s3object s WHERE (s."Região Origem" = 'SUL' OR s."Região Destino" = 'NORDESTE') OR (s."Região Origem" = 'NORDESTE' AND s."Região Destino" = 'SUDESTE')